export const createId = () => {
    const length = 10,
          numberChars = "1234567890",
          upperChars = "ABCDEFGHIJKOLMNPQRSTUVWXYZ",
          lowerChars = upperChars.toLowerCase(),
          symbolChars = "!@#$%^&*()_+",
          allChars = numberChars + upperChars + lowerChars + symbolChars;

    let randomString = "";

    for (let i = 0; i < length; i++) {
        let randomNumber = Math.floor(Math.random() * allChars.length);
        randomString += allChars[randomNumber];
    }

    return randomString;
}

