const getAge = (dateBirth) => {
      const [d, m, y] = dateBirth.split('.').map(Number); //подсмотрел
      const dateBegin = new Date(y, m - 1, d); //подсмотрел
      const dateNow = new Date();
      let age = dateNow.getFullYear() - dateBegin.getFullYear();
      const month = dateNow.getMonth() - dateBegin.getMonth();
      const day = dateNow.getDate() - dateBegin.getDate();
      if (month < 0 || (month === 0 && day < 0)) {
      age--;
      }
      return age
}

module.exports = getAge;