import {createFirstNameOrLastName} from "./createFirstNameOrLastName.mjs";
import {getAge} from "./getAge.mjs";
import {createId} from "./createId.mjs";


export const createNewObject = (obj) => {
    const user = obj.name.split(" "),
          firstName =  createFirstNameOrLastName(user,0),
          id = createId(),
          lastName = createFirstNameOrLastName(user,1),
          age = getAge(obj.dateBirth),
          purpose = obj.purpose.charAt(0).toUpperCase() + obj.purpose.slice(1);
    console.log (
        {
            id,
            firstName,
            lastName,
            dateBirth: obj.dateBirth,
            age,
            purpose
        }
    );
}